import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup,Validators } from "@angular/forms";
import { UserService } from '../../common/user.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  constructor(private userService: UserService, private router:Router){}

  ngOnInit(){
    // this.userService.userBasket.subscribe((data)=>{
    //   console.log('subscribed from login', data);
    // })
  }

  loginForm = new FormGroup({
    'username': new FormControl(null, [Validators.required]),
    'password': new FormControl(null, [Validators.required])
})
checkLogin(){
    const regValues:any = this.loginForm.value;
    this.loginForm.markAllAsTouched();
    if (this.loginForm.valid) {
      console.log('regValues', regValues);
      this.userService.loginUser(regValues).subscribe((data)=>{
        console.log('data in login', data);
        if (data) {
          const data1 = (data as any).user;
          const token = (data as any).token;
          localStorage.setItem("id_token", token);
          this.router.navigate(['/dashboard-module']);
      }
      })
    }
  }
}

<section class="vh-100" style="background-color: #eee;">
    <div class="container h-100">
      <div class="row d-flex justify-content-center align-items-center h-100">
        <div class="col-lg-12 col-xl-11">
          <div class="card text-black" style="border-radius: 25px;">
            <div class="card-body p-md-5">
              <div class="row justify-content-center">

                <div class="col-md-10 col-lg-6 col-xl-7 order-2 d-flex align-items-center order-lg-1">

                  <img src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-registration/draw1.webp"
                    class="img-fluid" alt="Sample image">

                </div>

                <div class="col-md-10 col-lg-6 col-xl-5  order-1 order-lg-2" >

                  <form [formGroup]="loginForm">

                    <div class="d-flex align-items-center mb-3 pb-1">
                      <i class="fas fa-cubes fa-2x me-3" style="color: #ff6219;"></i>
                      <span class="h1 fw-bold mb-0">Chandra's Logo</span>
                    </div>

                    <h5 class="fw-normal mb-3 pb-3" style="letter-spacing: 1px;">Sign into your account</h5>

                    <div class="form-outline mb-4">
                      <label class="form-label" for="username">User name</label>
                      <input formControlName="username" type="text" id="username" class="form-control form-control-lg" />
                      <span class="text-danger"  *ngIf="loginForm.controls['username'].touched && loginForm.controls['username'].errors">
                        this field is required*
                      </span>

                    </div>

                    <div class="form-outline mb-4">
                      <label class="form-label" for="password">Password</label>
                      <input formControlName="password"  type="password" id="password" class="form-control form-control-lg" />
                      <span class="text-danger"  *ngIf="loginForm.controls['password'].touched && loginForm.controls['password'].errors">
                        this field is required*
                      </span>
                    </div>

                    <div class="pt-1 mb-4">
                      <button class="btn btn-dark btn-lg btn-block" type="button" (click)="checkLogin()">Login</button>
                    </div>

                    <a class="small text-muted" href="#!">Forgot password?</a>
                    <p class="mb-5 pb-lg-2" style="color: #393f81;">Don't have an account? <a 
                        style="color: #393f81;" routerLink="/register">Register here</a></p>
                    <a href="#!" class="small text-muted">Terms of use.</a>
                    <a href="#!" class="small text-muted">Privacy policy</a>
                  </form>

                </div>

            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
