import { Component } from '@angular/core';
import { AbstractControl, FormBuilder, ValidatorFn, Validators } from '@angular/forms';

@Component({
  selector: 'app-r-builder-login',
  templateUrl: './r-builder-login.component.html',
  styleUrls: ['./r-builder-login.component.css']
})
export class RBuilderLoginComponent {
  constructor(private fb: FormBuilder){}
  loginForm = this.fb.group({
    email:[null, Validators.required],
    password:[null, [this.pwdValidater]]
  });
  getLoginData() {
    console.log(this.loginForm.value)
  }

  // pwdValidater(control: AbstractControl): { [key: string]: boolean } | null {
  //   console.log(control.value);
  //   if (control.value === 'chandra') {
  //     return { 'pwdValid': true };
  //   }
  //   return null;
  // }

  pwdValidater(pVal:string): ValidatorFn{
    return (control: AbstractControl):{[key:string]:boolean}| null{
      if(control.value === pVal){
        return {'pwdValid': true}
      } else{
        return null;
      }
    }
  }




}
<form [formGroup]="loginForm" (ngSubmit)="getLoginData()">
    <input type="text" formControlName="email" placeholder="Enter Email"/>
    <span *ngIf="loginForm.controls['email'].touched && loginForm.controls['email'].errors && loginForm.controls['email'].errors.required">
        please enter email
    </span><br /><br />
    <input type="password" formControlName="password" placeholder="password"/>

    <span *ngIf="loginForm.controls['password'].errors">
        enter valid
    </span>

    <button>Submit</button>
</form>
