import { Directive, ElementRef } from "@angular/core";

@Directive({
    selector:'[capitalFirst]'
})
export class CapitalFirst{
    constructor(private element: ElementRef){}
    ngOnInit(){
        this.element.nativeElement.style.backgroundColor = 'blue';
    }
}
