import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-r-login',
  templateUrl: './r-login.component.html',
  styleUrls: ['./r-login.component.css']
})
export class RLoginComponent {
//  email = new FormControl('');
//  password = new FormControl('');
loginForm = new FormGroup({
  email: new FormControl(null, Validators.required),
  password: new FormControl('')
})
loginUser(){
  console.log(this.loginForm.value)
}
get f(){
  return this.loginForm.controls;
}
}
<!--<input type="text" [formControl]="email" placeholder="please enter email" />
{{email.value}}
<input type="password" [formControl]="password" placeholder="please enter password" />-->
<form [formGroup]="loginForm" (ngSubmit)="loginUser()">
    <input type="text" formControlName="email" placeholder="please enter email" />
    <span *ngIf="!loginForm.get('email')">please eneter valid</span>
    {{loginForm.value | json}}
    <input type="password" formControlName="password" placeholder="please enter password" />
    <button class="btn btn-success">Login</button>
</form>
